tring.format","","function CustomDevice:updateText(text_id, format, value)","  local text = (","    type(value) ~= 'boolean'","    and SPRINTF(format, value)","    or (value and txt.yes or txt.no)","  )","  self:getElement(text_id):setValue('value', text, true)","end","","local function setrelay(signal, relayname)","  if signal then ","    if relayname:getValue(\"state\") ~= \"on\" then ","      relayname:call(\"turn_on\")","    end","  else","    if relayname:getValue(\"state\") ~= \"off\" then ","      relayname:call(\"turn_off\")","    end","  end","end","","--harmatpont és abszolut páratartalom","","-- Constants for psychrometric calculations","local A = 6.112","local B = 17.67","local C = 243.5","local MW_RATIO = 2.1674","local KELVIN_OFFSET = 273.15","","-- Saturation vapor pressure (hPa)","local function saturation_vapor_pressure(temp_c)","    return A * math.exp((B * temp_c) / (temp_c + C))","end","","-- Calculate absolute humidity from T and RH","local function calculate_absolute_humidity(temp_c, rh)","    local svp = saturation_vapor_pressure(temp_c)","    local avp = (rh / 100.0) * svp","    return (avp * MW_RATIO) / (KELVIN_OFFSET + temp_c)","end","","-- Calculate RH from AH and T","local function calculate_rh(temp_c, target_ah)","    local svp = saturation_vapor_pressure(temp_c)","    local avp = (target_ah * (KELVIN_OFFSET + temp_c)) / MW_RATIO","    return (avp / svp) * 100.0","end","","-- Calculate T from AH and RH using numerical method","local function calculate_temp_from_ah_and_rh(target_ah, rh)","    local guess = -30.0","    local max_temp = 60.0","    local step = 0.01","    local tolerance = 0.001","","    while guess \u003c= max_temp do","        local svp = saturation_vapor_pressure(guess)","        local avp = (rh / 100.0) * svp","        local ah = (avp * MW_RATIO) / (KELVIN_OFFSET + guess)","","        if math.abs(ah - target_ah) \u003c tolerance then","            return guess","        end","","        guess = guess + step","    end","","    return nil -- not found","end","","function calc_dew_point(temp, rh)","  if temp == nil or rh == nil then","    return nil, \"Missing input\"","  end","  if rh \u003e 100 then","    rh=99","  elseif rh \u003c= 0 then ","    rh= 1","  end","","  local a = 17.62","  local b = 243.12","","  local gamma = (a * temp) / (b + temp) + math.log(rh / 100)","  local dew_point = (b * gamma) / (a - gamma)","","  return dew_point","end","","local function ah_dp_set()","  local ah_cel = calculate_absolute_humidity(kamra_cel_homerseklet_v1:getValue()/10,kamra_cel_para_v1:getValue()/10)","  local dp_cel = calc_dew_point(kamra_cel_homerseklet_v1:getValue()/10,kamra_cel_para_v1:getValue()/10)","  local ah_dp\t= {","    ah_cel = ah_cel,","    ah_befujt_cel = ah_cel,","    ah_befujt = ah_cel,","    ah_kamra = ah_cel,","    ah_kulso = ah_cel,","    dp_cel = dp_cel,","    dp_befujt_cel = dp_cel,","    dp_befujt = dp_cel,","    dp_kamra = dp_cel,","    dp_kulso = dp_cel","  }","  ah_dp_table1:setValue(ah_dp,true)","end","","local function ah_dp_cel_szamol(dp_tx, ah_tx)","  local ah = calculate_absolute_humidity(kamra_cel_homerseklet_v1:getValue()/10,kamra_cel_para_v1:getValue()/10)","  local dp = calc_dew_point(kamra_cel_homerseklet_v1:getValue()/10,kamra_cel_para_v1:getValue()/10)","  ah_dp_table1:setValueByPath(\"ah_cel\",ah,true)","  ah_dp_table1:setValueByPath(\"dp_cel\",dp,true)","  dp_tx:setValue(\"value\", string.format(\"harmatpont:  %3.1f°C\", dp),true)","  ah_tx:setValue(\"value\", string.format(\"absolut pára:  %0.3fg/m3\", ah),true)","end","","local function ah_dp_befujt_szamol()","  local ah = calculate_absolute_humidity(befujt_homerseklet_akt1:getValue()/10,befujt_para_akt1:getValue()/10)","  local dp = calc_dew_point(befujt_homerseklet_akt1:getValue()/10,befujt_para_akt1:getValue()/10)","  ah_dp_table1:setValueByPath(\"ah_befujt\",ah,true)","  ah_dp_table1:setValueByPath(\"dp_befujt\",dp,true)","end","","-- CORRECTED PSYCHROMETRIC EVALUATION for outdoor air benefit","-- Implements three-step method to avoid comparing RH at different temperatures","-- Reference: CORRECTED_PSYCHROMETRIC_EVALUATION.md","local function evaluate_outdoor_air_benefit(","  chamber_temp,     -- Current chamber temperature (°C)","  chamber_rh,       -- Current chamber RH (%)","  target_temp,      -- Target chamber temperature (°C)","  target_rh,        -- Target chamber RH (%)","  outdoor_temp,     -- Outdoor temperature (°C)","  outdoor_rh,       -- Outdoor RH (%)","  outdoor_mix_ratio -- Outdoor air mixing ratio (0.0-1.0, e.g., 0.3 = 30%)",")","  -- STEP 1: Calculate absolute humidities (temperature-independent metric)","  local chamber_ah = calculate_absolute_humidity(chamber_temp, chamber_rh)","  local target_ah = calculate_absolute_humidity(target_temp, target_rh)","  local outdoor_ah = calculate_absolute_humidity(outdoor_temp, outdoor_rh)","","  -- STEP 2: Calculate mixed air properties (assuming perfect mixing)","  local mixed_temp = chamber_temp * (1 - outdoor_mix_ratio) + outdoor_temp * outdoor_mix_ratio","  local mixed_ah = chamber_ah * (1 - outdoor_mix_ratio) + outdoor_ah * outdoor_mix_ratio","","  -- STEP 3: Project final steady-state at target temperature","  -- Calculate what RH would be at target temperature with mixed absolute humidity","  local projected_rh_at_target = calculate_rh(target_temp, mixed_ah)","","  -- DECISION CRITERIA (corrected - comparing at same temperature):","  -- 1. Temperature benefit: Moving toward target?","  local temp_delta_current = math.abs(target_temp - chamber_temp)","  local temp_delta_mixed = math.abs(target_temp - mixed_temp)","  local temp_improves = temp_delta_mixed \u003c temp_delta_current","","  -- 2. Humidity evaluation at target temperature (NOT at mixed temperature!)","  -- Allow ±5% RH tolerance to avoid rejecting beneficial temperature improvements","  local rh_tolerance = 5.0","  local rh_acceptable = math.abs(projected_rh_at_target - target_rh) \u003c= rh_tolerance","","  -- 3. Absolute humidity check: Is mixed AH closer to target than current?","  local ah_delta_current = math.abs(target_ah - chamber_ah)","  local ah_delta_mixed = math.abs(target_ah - mixed_ah)","  local ah_improves = ah_delta_mixed \u003c ah_delta_current  -- Strict improvement, not just \"not worse\"","","  -- CORRECTED DECISION LOGIC:","  -- Use outdoor air if temperature improves AND (humidity improves OR remains acceptable)","  local beneficial = temp_improves and (ah_improves or rh_acceptable)","","  -- Debug output (can be removed in production)","  if beneficial then","    print(string.format(\"Outdoor air BENEFICIAL: Temp %.1f→%.1f°C (target %.1f°C), \" ..","                        \"RH@target %.1f%% (target %.1f%%), AH %.2f→%.2f g/m³ (target %.2f g/m³)\",","                        chamber_temp, mixed_temp, target_temp,","                        projected_rh_at_target, target_rh,","                        chamber_ah, mixed_ah, target_ah))","  end","","  return beneficial, {","    mixed_temp = mixed_temp,","    mixed_ah = mixed_ah,","    projected_rh_at_target = projected_rh_at_target,","    temp_improves = temp_improves,","    ah_improves = ah_improves,","    rh_acceptable = rh_acceptable","  }","end","","--Fő szabályozási ciklus","function CustomDevice:controlling()","","  local kamra_homerseklet     = kamra_homerseklet_v1:getValue()\t--\tint*10","  local kamra_para            = kamra_para_v1:getValue()\t--\tint*10","  local kamra_cel_homerseklet  = kamra_cel_homerseklet_v1:getValue()\t--\tint*10","  local kamra_cel_para\t    \t = kamra_cel_para_v1:getValue()\t--\tint*10","  local befujt_cel_homerseklet = befujt_cel_homerseklet_v1:getValue()\t--\tint*10","  local befujt_cel_para\t\t     = befujt_cel_para_v1:getValue()\t--\tint*10","  local kulso_homerseklet\t     = kulso_homerseklet_v1:getValue()\t--\tint*10","  local kulso_para\t\t\t       = kulso_para_v1:getValue()\t--\tint*10","  local befujt_mert_para       = befujt_para_akt1:getValue()","  local befujt_mert_homerseklet = bef