dis = signal.cool_dis","  local sleep = signal.sleep","  local sum_wint_jel = signal.sum_wint_jel","  local humi_save = signal.humi_save","  local add_air_max = signal.add_air_max","","  local kamra_hibaflag","  if kamra_hibaszam1:getValue()\u003c= 0 then ","    kamra_hibaflag=true","  else ","    kamra_hibaflag=false","  end","  ","  -- 1. befujt cél értékek számítása","  -- Store old values to detect meaningful changes","  local old_befujt_cel_homerseklet = befujt_cel_homerseklet_v1:getValue()","  local old_befujt_cel_para = befujt_cel_para_v1:getValue()","  ","  if kamra_hibaflag then ","    befujt_cel_para = kamra_cel_para_v1:getValue() ","  else","    befujt_cel_para = kamra_cel_para + (kamra_cel_para - kamra_para)/2","  end","  ","  if kamra_hibaflag then ","    befujt_cel_homerseklet = kamra_cel_homerseklet_v1:getValue() ","  else","    befujt_cel_homerseklet = kamra_cel_homerseklet + (kamra_cel_homerseklet - kamra_homerseklet)/2","  end","  ","  -- INTELLIGENT PROPAGATION: Only propagate if values changed meaningfully","  local temp_changed = math.abs(befujt_cel_homerseklet - old_befujt_cel_homerseklet) \u003e= TEMP_CHANGE_THRESHOLD","  local humi_changed = math.abs(befujt_cel_para - old_befujt_cel_para) \u003e= HUMI_CHANGE_THRESHOLD","  ","  befujt_cel_homerseklet_v1:setValue(befujt_cel_homerseklet, not temp_changed)","  befujt_cel_para_v1:setValue(befujt_cel_para, not humi_changed)","  ","  --Befujt cél dp és ah számolása","  local ah = calculate_absolute_humidity(befujt_cel_homerseklet_v1:getValue()/10,befujt_cel_para_v1:getValue()/10)","  local dp = calc_dew_point(befujt_cel_homerseklet_v1:getValue()/10,befujt_cel_para_v1:getValue()/10)","  ah_dp_table1:setValueByPath(\"dp_befujt_cel\",dp,true)","  ah_dp_table1:setValueByPath(\"ah_befujt_cel\",ah,true)","  ah_dp_table1:save(true)","","  -- 2.\tszabályozás kamra_cel_ értékekre (hőmérséklet)","  if not kamra_hibaflag then ","    if kamra_homerseklet \u003e(kamra_cel_homerseklet+2*deltahi_kamra_homerseklet) then ","      kamra_hutes =true ","    end","    if kamra_homerseklet \u003c(kamra_cel_homerseklet+deltahi_kamra_homerseklet) then ","      kamra_hutes =false ","    end","    if kamra_homerseklet \u003e(kamra_cel_homerseklet-deltalo_kamra_homerseklet) then ","      kamra_hutes_tiltas =false","      kamra_futes =false ","    end","    if kamra_homerseklet \u003c(kamra_cel_homerseklet-2*deltalo_kamra_homerseklet) then  ","      kamra_futes =true","    end","    if kamra_homerseklet \u003c(kamra_cel_homerseklet-3*deltalo_kamra_homerseklet) then ","      kamra_hutes_tiltas =true","    end","","    -- (pára tartalom)","    if kamra_para \u003e(kamra_cel_para+2*deltahi_kamra_para) then ","      kamra_para_hutes =true","    end","    if kamra_para \u003c(kamra_cel_para+deltahi_kamra_para) then ","      kamra_para_hutes =false ","    end","    if kamra_para \u003e(kamra_cel_para-deltalo_kamra_para) then ","      kamra_para_futes_tiltas =false ","    end","    if kamra_para \u003c(kamra_cel_para-2*deltalo_kamra_para) then ","      kamra_para_futes_tiltas =true\t","    end","  else ","    kamra_hutes =false","    kamra_hutes_tiltas =false","    kamra_futes =false","    kamra_para_hutes =false","    kamra_para_futes_tiltas =false","  end  ","","  --3.\tszabályozás befujt_cel_ értékekre\t","  if befujt_mert_homerseklet \u003e (befujt_cel_homerseklet+deltahi_befujt_homerseklet) then ","    befujt_hutes =true","    befujt_futes =false","  end","  if befujt_mert_homerseklet \u003e befujt_cel_homerseklet then ","    befujt_futes =false ","  end","  if befujt_mert_homerseklet \u003c befujt_cel_homerseklet then ","    befujt_hutes =false ","  end","  if befujt_mert_homerseklet \u003e(befujt_cel_homerseklet - deltalo_befujt_homerseklet) then ","    hutes_tiltas =false ","  end","  if befujt_mert_homerseklet \u003c(befujt_cel_homerseklet - deltalo_befujt_homerseklet) then ","    befujt_futes =true ","    befujt_hutes =false ","  end","  if befujt_mert_homerseklet\t\u003c(befujt_cel_homerseklet - 2*deltalo_befujt_homerseklet) then ","    hutes_tiltas =true ","  end","  if befujt_mert_homerseklet \u003c MIN_SUPPLY_AIR_TEMP then ","    hutes_tiltas =true ","  end","","  if befujt_mert_para \u003e (befujt_cel_para+deltahi_befujt_para) then ","    befujt_para_hutes =true","    futes_tiltas =false ","  end","  if befujt_mert_para \u003e befujt_cel_para then ","    futes_tiltas =false ","  end","  if befujt_mert_para \u003c befujt_cel_para then ","    befujt_para_hutes =false ","  end","  if befujt_mert_para \u003c befujt_cel_para - deltalo_befujt_para then ","    futes_tiltas =true","  end","  ","  --szabályozási jel állítások vége ","","  local warm_1 = not(kamra_para_futes_tiltas or futes_tiltas) and (kamra_futes or befujt_futes)","  warm = warm_1 and (not signal.sleep)","","  cool = not(kamra_hutes_tiltas) and (kamra_hutes or befujt_hutes or kamra_para_hutes)","  local cool_rel = cool and (not signal.sleep) and signal.sum_wint_jel","","  warm_dis = kamra_para_futes_tiltas or futes_tiltas","  dehumi = kamra_para_hutes  or befujt_para_hutes","  cool_dis = kamra_hutes_tiltas","","  -- CORRECTED OUTDOOR AIR EVALUATION (replaces simple boolean logic)","  -- Old logic: signal.add_air_max = cool and (not signal.sum_wint_jel) and (not signal.humi_save)","  -- New logic: Psychrometric evaluation to determine actual benefit","  local outdoor_air_beneficial = false","  if not signal.humi_save then  -- Only evaluate if not in humidity save mode","    -- Assume 30% outdoor air mixing ratio (configurable)","    local outdoor_mix_ratio = 0.30","","    -- Evaluate if outdoor air is beneficial using corrected psychrometric logic","    outdoor_air_beneficial = evaluate_outdoor_air_benefit(","      kamra_homerseklet / 10,      -- Current chamber temp (°C)","      kamra_para / 10,              -- Current chamber RH (%)","      kamra_cel_homerseklet / 10,   -- Target chamber temp (°C)","      kamra_cel_para / 10,          -- Target chamber RH (%)","      kulso_homerseklet / 10,       -- Outdoor temp (°C)","      kulso_para / 10,              -- Outdoor RH (%)","      outdoor_mix_ratio             -- 30% outdoor air","    )","  end","","  signal.add_air_max = outdoor_air_beneficial and (not signal.sum_wint_jel)","  signal.reventon = signal.humi_save","  signal.add_air_save = signal.humi_save","  signal.bypass_open = signal.humi_save or (cool and not dehumi)","  signal.main_fan = signal.sum_wint_jel","","  -- HUMIDIFICATION CONTROL LOGIC","  -- Independent of summer/winter mode (sum_wint_jel)","  -- Start: when current RH projected to target temp is 5% below target","  -- Stop: when current absolute humidity exceeds target absolute humidity","  if not kamra_hibaflag then","    -- Calculate absolute humidities","    local chamber_ah = calculate_absolute_humidity(kamra_homerseklet / 10, kamra_para / 10)","    local target_ah = calculate_absolute_humidity(kamra_cel_homerseklet / 10, kamra_cel_para / 10)","","    -- Project current AH to what RH would be at target temperature","    local projected_rh_at_target = calculate_rh(kamra_cel_homerseklet / 10, chamber_ah)","","    -- Start humidification if projected RH is 5% below target","    if projected_rh_at_target \u003c (kamra_cel_para / 10 - 5.0) then","      kamra_humidification = true","    end","","    -- Stop humidification if current absolute humidity equals or exceeds target","    if chamber_ah \u003e= target_ah then","      kamra_humidification = false","    end","  else","    kamra_humidification = false","  end","","  signal.humidification = kamra_humidification","","  --relék állapotának aktualizálása ( warm, cool, humidifier)","  setrelay(warm, relay_warm)","  setrelay(cool_rel, relay_cool)","  setrelay(signal.add_air_max, relay_add_air_max)","  setrelay(signal.reventon, relay_reventon)","  setrelay(signal.add_air_save, relay_add_air_save)","  setrelay(signal.bypass_open, relay_bypass_open)","  setrelay(signal.main_fan, relay_main_fan)","  setrelay(signal.humidification, relay_humidifier)","","  -- widget kimenetek aktualizálása","  local output_text = \" \"","  if warm then output_text =\"Fűtés Aktív!\" else  output_text = \" \" end","  self:getElement('text_input_0_warm'):setValue(\"value\",  output_text,true)","  if cool then output_text =\"Hűtés Aktív!\" else  output_text = \" \" end","  self:getElement('text_input_1_cool'):setValue(\"value\",  output_text,true)","  if warm_dis then output_text =\"Fűtés Tiltva!\" else  output_text = \" \" end","  self:getElement('text_input_2_wdis'):setValue(\"value\",  output_text,true)","","  -- Humidity control display (dehumidification and humidification share same widget)","  -- Priority: cool_dis \u003e dehumi \u003e humidification","  if cool_dis then","    output_text =\"Hűtés Tiltva!\"","  elseif dehumi then","    output_text =\"Páramentesítés!\"","  elseif signal.humidification then","    output_text =\"Párásítás Aktív!\"","  else","    output_text = \" \"","  end","  self:getElement('text_input_3_cdis'):setValue(\"value\",  output_text,true)","  ","  --jelzések és flag-ek - CRITICAL: Store old values to detect changes","  local old_signal = signals1:getValue({})","  ","  signal.warm_dis = warm_dis","  signal.dehumi = dehumi ","  signal.cool = cool","  signal.warm = warm","  signal.cool_dis = cool_dis","","  -- INTELLIGENT PROPAGATION: Only propagate if signal state actually changed","  local signal_changed = (","    old_signal.warm_dis ~= signal.warm_dis or","    old_signal.dehumi ~= signal.dehumi or","    old_signal.cool ~= signal.cool or","    old_signal.warm ~= signal.warm or","    old_signal.cool_dis ~= signal.cool_dis or","    old_signal.add_air_max ~= signal.add_air_max or","    old_signal.reventon ~= signal.reventon or","    old_signal.add_air_save ~= signal.add_air_save or","    old_signal.bypass_open ~= signal.bypass_open or","    old_signal.main_fan ~= signal.main_fan or","    old_signal.humidification ~= signal.humidification","  )","  ","  signals1:setValue(signal, not signal_changed)  -- Propagate only if changed","","end --controlling vége","","","-- REFACTORED: Moving average with intelligent propagation","-- Propagates event only when:","-- 1. Buffer is full (has mertdb measurements)","-- 2. Value has changed significantly","local function mozgoatlag(tablazat, akt_meres, atlag_ertek, mertdb, kiir_call, simulate)","  local instab = {}","  for ind, value in pairs(tablazat:getValue({})) do","    table.insert(instab, value)","  end","  ","  table.insert(instab, akt_meres)        ","  if #instab \u003e mertdb then","    table.remove(instab, 1)","  end","  ","  local sum = 0","  for _, value in ipairs(instab) do","    sum = sum + value","  end","  ","  -- Always update the table buffer (internal use)","  tablazat:setValue(instab, kiir_call)","  ","  if not simulate then  ","    local new_avg = math.floor((sum / #instab) + 0.5)  -- Round to nearest integer","    local old_avg = atlag_ertek:getValue()","    ","    -- INTELLIGENT PROPAGATION CONDITIONS:","    -- 1. Buffer must be full (has enough measurements for valid average)","    -- 2. Value must have changed meaningfully","    local buffer_ready = (#instab \u003e= mertdb)","    local value_changed = math.abs(new_avg - old_avg) \u003e= 1  -- At least 0.1 degree/% change","    ","    local should_propagate = buffer_ready and value_changed","    ","    atlag_ertek:setValue(new_avg, not should_propagate)","    ","    if should_propagate then","      print(\"Moving average propagated:\", atlag_ertek, \"old:\", old_avg, \"new:\", new_avg)","    end","  end","  ","  if not kiir_call then","    print(tablazat)","  end","end","","","-- frissítés 5000 msec - 5 sec","function CustomDevice:onEvent(event)","  local source = event.source","  local det = event.details","","  local timerState = self:getComponent(\"timer\"):getState() ","  ","  -- Timer-based polling and control cycle","  if (timerState == \"elapsed\" or timerState == \"off\") then","    self:getComponent(\"timer\"):start(5000)","    self:poll()","    self:controlling()","    print(befujt_homerseklet_akt1:getValue(), befujt_para_akt1:getValue(), befujt_szimulalt1:getValue())","    ","  -- Process Modbus read response","  elseif event.type == 'modbus_client_async_read_response' then","    local profiler1 = utils:profiler()","    profiler1:start()","    ","    local var1, var2","    self:c():onRegisterAsyncRead(function (kind, addrBase, values)","      self:online()","      ","      local function value(addr)","        if type(values) == 'table' then","          return values[addr - addrBase + 1]","        else","          return values","        end","      end","      ","      if kind == 'INPUT_REGISTERS' then     ","        if addrBase == 1 then","          var1 = value(1)","          var2 = value(2)","        end","      end","    end)","    ","    -- REFACTORED: mozgoatlag now handles intelligent propagation internally","    mozgoatlag(befujt_homerseklet_mert_table1, var1, befujt_homerseklet_akt1, 3, true, befujt_szimulalt1:getValue())","    mozgoatlag(befujt_para_mert_table1, var2, befujt_para_akt1, 3, befujt_szimulalt1:getValue(), befujt_szimulalt1:getValue())   ","    ah_dp_befujt_szamol()","    ","    profiler1:stop()","    profiler1:print()","","  -- React to sensor value changes from other devices or propagated events","  elseif event.type == \"lua_variable_state_changed\" then","    -- Handle different variable changes","    if source.id == 23 or source.id == 24 then  -- befujt_homerseklet_akt1 or befujt_para_akt1","      self:getElement('_1_tx_befujt_homerseklet_'):setValue(\"value\", string.format(\"%3.1f°C\", befujt_homerseklet_akt1:getValue()/10), true)","      self:getElement('_tx_2_tx_befujt_para'):setValue(\"value\", string.format('%3.1f%%', befujt_para_akt1:getValue()/10), true)","      self:getElement(\"dp_befujt_tx\"):setValue(\"value\", string.format(\"harmatpont:  %3.1f°C\", ah_dp_table1:getValue().dp_befujt), true)","      self:getElement(\"ah_befujt_tx\"):setValue(\"value\", string.format(\"absolut pára:  %0.3fg/m3\", ah_dp_table1:getValue().ah_befujt), true)","      print(event.type, source.id, source.type, det, \"p1 - befujt values changed\")","      ","    elseif source.id == 1 or source.id == 2 or source.id == 19 or source.id == 20 then  -- kamra values","      local kamra_para = kamra_para_v1:getValue()","      local kamra_homerseklet = kamra_homerseklet_v1:getValue()","      ","      self:getElement(\"_3_tx_kamra_homerseklet_\"):setValue(\"value\", string.format(\"%3.1f°C\", kamra_homerseklet/10), true)","      self:getElement(\"_4_tx_kamra_para_\"):setValue(\"value\", string.format(\"%3.1f%%\", kamra_para/10), true)","      self:getElement(\"dp_kamra_tx\"):setValue(\"value\", string.format(\"harmatpont:  %3.1f°C\", ah_dp_table1:getValue().dp_kamra), true)","      self:getElement(\"ah_kamra_tx\"):setValue(\"value\", string.format(\"absolut pára:  %0.3fg/m3\", ah_dp_table1:getValue().ah_kamra), true)","      print(event.type, source.id, source.type, det, \"p2 - kamra values changed\")","      ","    elseif source.id == 3 or source.id == 4 then  -- kamra_cel_homerseklet_v1 or kamra_cel_para_v1 changed","      -- BUG FIX: Update sliders to match the NEW variable values (not overwrite with old slider values)","      local celh = kamra_cel_homerseklet_v1:getValue()","      local celp = kamra_cel_para_v1:getValue()","","      self:getElement(\"slider_1\"):setValue(\"value\", celh/10, true)","      self:getElement(\"slider_0\"):setValue(\"value\", celp/10, true)","","      -- Update dew point and absolute humidity displays","      ah_dp_cel_szamol(self:getElement(\"dp_cel_tx\"), self:getElement(\"ah_cel_tx\"))","","      print(event.type, source.id, source.type, det, \"p3 - target setpoint changed, sliders updated\")","      ","    elseif source.id == 7 or source.id == 8 or source.id == 21 or source.id == 22 then  -- kulso values","      self:getElement(\"_3_tx_kulso_homerseklet_\"):setValue(\"value\", string.format(\"%3.1f°C\", kulso_homerseklet_v1:getValue()/10), true)","      self:getElement(\"_4_tx_kulso_para_\"):setValue(\"value\", string.format(\"%3.1f%%\", kulso_para_v1:getValue()/10), true)","      ","      local ah_k = calculate_absolute_humidity(kulso_homerseklet_v1:getValue()/10, kulso_para_v1:getValue()/10)","      local dp_k = calc_dew_point(kulso_homerseklet_v1:getValue()/10, kulso_para_v1:getValue()/10)","      ah_dp_table1:setValueByPath(\"ah_kulso\", ah_k, true)","      ah_dp_table1:setValueByPath(\"dp_kulso\", dp_k, true)","      self:getElement(\"dp_kulso_tx\"):setValue(\"value\", string.format(\"harmatpont: %3.1f°C\", dp_k), true)","      self:getElement(\"ah_kulso_tx\"):setValue(\"value\", string.format(\"absolut pára: %0.3fg/m3\", ah_k), true)","      print(event.type, source.id, source.type, det, \"p4 - outdoor values changed\")","      ","    elseif source.id == 34 then  -- signals1 changed - CRITICAL","      print(\"SIGNAL STATE CHANGED - Reloading control signals from other device\")","      -- Re-run control algorithm with updated signals","      self:controlling()","    end","","  elseif event.type == 'modbus_client_async_write_response' then","    self:c():onRegisterAsyncWrite(function ()","      self:online()","    end)","","  elseif event.type == 'modbus_client_async_request_failure' then","    self:c():onAsyncRequestFailure(function (req, err, kind, addrBase)","      utils:printf('Failed to %s %s: %s', req, kind, err)","      if err == 'TIMEOUT' then","        local befujt_hiba = befujt_hibaszam1:getValue()","        if befujt_hiba \u003e 0 then ","          befujt_hiba = befujt_hiba - 1","          befujt_hibaszam1:setValue(befujt_hiba, true)","        end ","      end","    end)","  end","  ","  local source = event.source","  local det = event.details","  print(event.type, source.id, source.type, det)","end",""," ","","function CustomDevice:on_Target_TemperatureChange(newValue, element)","  local temp1 = kamra_cel_homerseklet_v1:getValue()","  if (temp1+19 \u00